mapper.delete:
DELETE FROM free_board
WHERE bno = #{bno}
AND pwd = #{pwd}

mapper.select:
SELECT COUNT(pwd) cnt
FROM free_board
WHERE bno = #{bno}
AND pwd = #{pwd}

mapper.update:
UPDATE free_board
SET title = #{title}
	, writer = #{writer}
	, cnts = #{cnts}
WHERE bno = #{bno}
AND pwd = #{pwd}

mapper.select:
select rno, bno, writer, cnts, write_date
from free_board_reply
where bno = #{bno}
